function matchPageTopToNavbar(){$('.navbar-fixed-top + .container').css('padding-top',$('header').height());}
function matchFooterHeight(){var newheight=$('.footer > .container').outerHeight();$footer=$('.footer');fpad=$footer.outerHeight()-$footer.height();$footer.height(newheight+fpad);padtop=parseInt($footer.css('padding-top'));padbottom=parseInt($footer.css('padding-bottom'));$('body').css('margin-bottom',$footer.outerHeight()+padtop+padbottom);}
$(window).load(function(){matchPageTopToNavbar();matchFooterHeight();});$(window).resize(function(){matchPageTopToNavbar();matchFooterHeight();});